import { State } from "../State";

export class SprintState extends State {
  team;
  scene;
  constructor(fsm, team, scene) {
    super(fsm);
    this.team = team;
    this.scene = scene;
  }

  enter() {
    this.scene.launch("SprintScene", {
      team: this.team,
      onClose: () => {
        this.fsm.next();
      },
    });
  }

  exit() {
    this.scene.stop("SprintScene");
  }
}
