import Phaser from "phaser";

export class CompanyMenu extends Phaser.GameObjects.Container {
  constructor(scene, x = 0, y = 0, company) {
    super(scene, x, y);
    const graphics = scene.add.graphics({
      x: 0,
      y: 0,
      fillStyle: { color: 0xff00ff, alpha: 0.6 },
      lineStyle: { color: 0x00ff00 },
    });
    graphics.lineBetween(400, 0, 400, 600);

    const menu = scene.add.image(0, 0, "menu");
    const button = scene.add.image(0, 75, "button");
    const text = scene.add
      .text(50, 68, "Accept Job", {
        font: "12px Open Sans",
        fill: "#ffffff",
      })
      .setOrigin(0.5);

    menu.setScale(1.5, 2);
    button.setScale(0.5);
    //text.setOrigin(-25, 0);

    this.setSize(100, 150);

    this.add([menu, button, text, graphics]);

    scene.add.existing(this);
  }
}
