import Phaser from "phaser";
import { Dev, ProductOwner, ScrumMaster, Tester } from "../classes/Employee";
import { randomInt } from "../utils/random";

export class MainScene extends Phaser.Scene {
  constructor() {
    super("MainScene");
  }

  init() {
    this.createStartingEmployees();
    this.vacancies = [
      new ScrumMaster(),
      new ScrumMaster(),
      new Tester(),
      new ProductOwner(),
    ];
  }

  // load assets
  preload() {
    // this.load.image("background", "assets/images/background-city.png");
    // this.load.audio("treeAudio", "assets/audio/arbol.mp3");
  }

  // executed once, after assets were loaded
  create() {
    console.log(this.employees);
  }

  update(time, delta) {}

  createStartingEmployees() {
    const teamSize = this.registry.get("settings").startingTeamSize;
    this.employees = Array(teamSize)
      .fill(null)
      .map(() => new Dev());
  }

  createStartingCandidates() {
    const scrumMasters = Array(randomInt(0, 2))
      .fill(null)
      .map(() => new ScrumMaster());
    const testers = Array(randomInt(0, 2))
      .fill(null)
      .map(() => new Tester());
    const productOwners = Array(randomInt(0, 2))
      .fill(null)
      .map(() => new ProductOwner());
    const devs = Array(randomInt(0, 2))
      .fill(null)
      .map(() => new Dev());
    const candidates = [scrumMasters, testers, productOwners, devs].flat();
    console.log(candidates);
    return candidates;
  }
}
