import Phaser from "phaser";
import { Card } from "../game-objects/Card";
import * as theme from "../theme";

export class SprintScene extends Phaser.Scene {
  intro = true;
  constructor() {
    super("SprintScene");
  }

  init() {}

  preload() {}

  create({ events, onClose }) {
    this.events = events;
    this.onClose = onClose;
    this.updateSprintNumber();
    this.createComponents();
    this.triggerEvents();
  }

  update(time, delta) {}

  updateSprintNumber() {
    this.sprintNumber = this.registry.inc("sprintNumber").get("sprintNumber");
    console.log("this.sprintNumber", this.sprintNumber);
  }

  createComponents() {
    this.header = this.add
      .text(400, 15, `Sprint ${this.sprintNumber}`, theme.h1)
      .setOrigin(0.5, 0);
    this.close = this.add
      .text(760, 10, "X", theme.x)
      .setOrigin(0)
      .setInteractive({ useHandCursor: true })
      .on("pointerup", () => {
        this.onClose();
      });
  }
}
