import Phaser from "phaser";
import { Button } from "./Button";
import * as theme from "../theme";

export class Card extends Phaser.GameObjects.Container {
  constructor(scene, x = 0, y = 0, content, callback) {
    super(scene, x, y);

    // console.log("CompanyMenu:preload");
    this.form = this.scene.add.dom(300, 400).createFromCache("vacancy");

    this.setTitle(content.title);
    this.setDescription(content.text);
    this.setSize(100, 150);

    this.card = this.scene.add.image(0, 0, "card").setOrigin(0.5, 0);

    this.createText(content);
    this.add([this.card, this.title, this.text]);

    this.createButton(content.buttonText, callback);

    this.scene.add.existing(this);
  }

  setTitle(title) {
    this.form.getChildByID("title").textContent = title;
  }

  setDescription(description) {
    this.form.getChildByID("description").textContent = description;
  }

  setContent() {}

  createText(content) {
    this.title = this.scene.add
      .text(0, 15, content.title, theme.cardTitle)
      .setOrigin(0.5, 0);
    this.text = this.scene.add
      .text(0, 40, content.text, theme.cardText)
      .setOrigin(0.5, 0);
  }

  createButton(text, callback) {
    if (callback) {
      const button = this.form.getChildByID("submit");
      button.value = text;
      this.button = this.scene.add.existing(
        new Button(this.scene, 0, 155, text, callback)
      );
      this.add(this.button);
    } else {
      this.form.getChildByID("submit").dispalay = "none";
    }
  }
}
