import { State } from "../State";

export class SprintEventState extends State {
  dialog;
  constructor(fsm, dialog, { text, A, B }) {
    super(fsm);
    this.dialog = dialog;
  }

  enter(text, yesCallback, noCallback) {
    this.dialog.getChildByID("description").textContent = text;
    this.dialog.setVisibility(true);
  }

  exit() {
    this.dialog.setVisibility(false);
  }
}
