import Phaser from "phaser";
import { Column, TextButton, TextSprite } from "phaser-ui-tools";

export class StartScene extends Phaser.Scene {
  constructor() {
    super("StartScene");
  }

  init() {}

  // load assets
  preload() {
    // this.load.image("background", "assets/images/background-city.png");
    // this.load.audio("treeAudio", "assets/audio/arbol.mp3");
  }

  // executed once, after assets were loaded
  create() {
    var textStyle = { fill: "#FFF", font: "16px Courier New" };
    this.header = new TextSprite(this, 0, 0, "header")
      .setText("Header", textStyle)
      .setOrigin(0.0, 0.0);

    var buttonOne = new TextButton(
      this,
      0,
      0,
      "button",
      newGameCallback,
      this,
      1,
      0,
      2,
      1
    )
      .setText("New Game", textStyle)
      .eventTextYAdjustment(3);
    var buttonTwo = new TextButton(
      this,
      0,
      0,
      "button",
      continueCallback,
      this,
      1,
      0,
      2,
      1
    )
      .setText("Continue", textStyle)
      .eventTextYAdjustment(3);
    var buttonThree = new TextButton(
      this,
      0,
      0,
      "button",
      optionsCallback,
      this,
      1,
      0,
      2,
      1
    )
      .setText("Options", textStyle)
      .eventTextYAdjustment(3);

    var column = new Column(this, 200, 100);
    column.addNode(buttonOne, 0, 10);
    column.addNode(buttonTwo, 0, 10);
    column.addNode(buttonThree, 0, 10);
  }

  update(time, delta) {}
}

function newGameCallback() {
  this.header.text.setText("You clicked the New Game button");
}

function continueCallback() {
  this.header.text.setText("You clicked the Continue button");
}

function optionsCallback() {
  this.header.text.setText("You clicked the Options button");
}
