import Phaser from "phaser";
import config from "../config.json";
import { CompanyMenu } from "../objects/CompanyMenu";

export class StartScene extends Phaser.Scene {
  constructor() {
    super("StartScene");
    console.log("StartScene");
  }

  init() {}

  // load assets
  preload() {
    this.load.image("button", "assets/UIpack/PNG/blue_button00.png");
    // this.load.audio("treeAudio", "assets/audio/arbol.mp3");
  }

  // executed once, after assets were loaded
  create() {
    console.log("create StartScene");
    this.companies = this.add.group(
      Array(config.companies.number)
        .fill(null)
        .forEach(() => {
          return new CompanyMenu(this);
        })
    );
  }

  update(time, delta) {}
}
