import { State } from "../State";

export class SprintResultsState extends State {
  card;
  constructor(machine, card, results, onClose) {
    super(machine);
    this.card = card;
    this.results = results;
    this.onClose = onClose;
  }

  enter() {
    const { commitment, velocity } = this.results;
    this.card.getChildByID(
      "description"
    ).textContent = `The team achieved ${this.results.velocity} this sprint.`;
    const close = this.card.getChildByID("close");
    close.addEventListener("click", () => {
      this.onClose();
    });
    this.card.setVisible(true);
  }

  exit() {
    this.card.setVisible(false);
  }
}
