import { State } from "../State";

export class SprintResultsState extends State {
  card;
  constructor(machine, card, results, onClose) {
    super(machine);
    this.card = card;
    this.results = results;
    this.onClose = onClose;
  }

  enter() {
    this.dialog.getChildByID(
      "description"
    ).textContent = `The team achieved ${this.results.velovity} this sprint.`;
    const A = this.dialog.getChildByID("A");
    const B = this.dialog.getChildByID("B");
    A.addEventListener("click", this.A);
    B.addEventListener("click", this.B);
    const close = this.card.getChildByName("close");
    this.card.setVisible(true);
  }

  exit() {
    this.card.setVisible(false);
  }
}
