import Phaser from "phaser";

export class CompanyMenu extends Phaser.GameObjects.Container {
  constructor(scene, x = 0, y = 0, company) {
    super(scene, x, y);

    const menu = scene.add.image(0, 0, "menu");
    const button = scene.add.image(0, 75, "button");
    const text = scene.add.text(0, 75, "Accept Job", {
      font: "12px Open Sans",
      fill: "#ffffff",
    });

    menu.setScale(1.5, 2);
    button.setScale(0.5);
    text.setDisplaySize(100, 30);

    this.setSize(100, 150);

    this.add([menu, button, text]);

    scene.add.existing(this);
  }
}
