import { State } from "./State";

export class LinearFSM {
  queue = [];
  constructor(states = [], delay = 1000) {
    if (states.some((state) => !this.isValid(state))) {
      throw new Error("LinearFSM expects an array of states");
    }
    this.delay = delay;
    this.queue = states;
  }

  add(state) {
    if (!this.isValid(state)) {
      throw new Error("add expects a Chapters");
    }
    this.queue.push(state);
  }

  play() {
    console.log("play", this.queue.length);
    if (this.queue.length) {
      const state = this.queue[0];
      state.enter();
    }
  }

  next() {}

  isValid(state) {
    return state instanceof State;
  }
}
